// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {CCIPReceiver} from "@chainlink/contracts-ccip/src/v0.8/ccip/applications/CCIPReceiver.sol";
import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";
import {GalNFT} from "./GalNFT.sol";
import "./IEscrow.sol";

contract DestinationMinter is CCIPReceiver {
    GalNFT nft;
    address esc;

    event MintCallSuccessfull();

    event MintTransferSuccessfull(
        address sender,
        uint256 tokenId,
        string tokenUri,
        address token,
        uint256 price,
        uint256 profit,
        bool check
    );

    constructor(
        address router,
        address nftAddress,
        address escrow
    ) CCIPReceiver(router) {
        nft = GalNFT(nftAddress);
        esc = escrow;
    }

    function decode(bytes memory approvePayload)
        public
        pure
        returns (
            address,
            uint256,
            string memory,
            address,
            uint256,
            uint256,
            bool
        )
    {
        require(approvePayload.length >= 4, "Payload is too short");
        bytes memory data = new bytes(approvePayload.length - 4);
        for (uint256 i = 4; i < approvePayload.length; i++) {
            data[i - 4] = approvePayload[i];
        }

        return
            abi.decode(
                data,
                (address, uint256, string, address, uint256, uint256, bool)
            );
    }

    function _ccipReceive(Client.Any2EVMMessage memory message)
        internal
        override
    {
        (
            address owner,
            uint256 tokenids,
            string memory uri,
            address tokens,
            uint256 amount,
            uint256 profits,
            bool checks
        ) = decode(message.data);

        IESCROW afd = IESCROW(esc);
        bool exist = afd.nftExist(address(nft), tokenids, owner);
        if (!exist) {
            emit MintTransferSuccessfull(owner,tokenids,uri,tokens,amount,profits,checks);
        } if(exist) {
            (bool success, ) = address(nft).call(message.data);
            require(success);
            emit MintCallSuccessfull();
        }
    }
}
